const buttons=document.querySelectorAll(".slider_controls button"),slides=document.querySelectorAll(".slider_item");let addButtonClickHandler=function(t,e){t.addEventListener("click",function(s){s.preventDefault(),buttons.forEach(t=>t.classList.remove("current")),slides.forEach(t=>t.classList.remove("slide_current")),t.classList.add("current"),e.classList.add("slide_current")})};for(i=0;i<buttons.length;i++)addButtonClickHandler(buttons[i],slides[i]);